<?phpfunction theme_options_admin_enqueue() {	    $template_url = get_template_directory_uri();    //CSS    wp_enqueue_style('thickbox');	    wp_enqueue_style('theme-colorpicker-css', $template_url.'/framework/admin/layout/css/colorpicker.css', false, false, 'screen');    wp_enqueue_style('theme-css', $template_url.'/framework/admin/layout/css/admin.css', false, false, 'screen');	wp_enqueue_style('prettyphoto-css', $template_url.'/framework/admin/layout/css/prettyPhoto.css', false, false, 'screen');	wp_enqueue_style('selectbox-css', $template_url.'/framework/admin/layout/css/jquery.selectBox.css', false, false, 'screen');	wp_enqueue_style('datepicker-css', $template_url.'/framework/admin/layout/css/datepicker/jquery.ui.all.css', false, false, 'screen');	    //JS	    wp_enqueue_script('jquery-ui-sortable');    wp_enqueue_script('thickbox');    wp_enqueue_script('media-upload');	wp_enqueue_script('theme_js', $template_url . '/framework/admin/layout/js/admin.js', array('jquery'), false );    wp_enqueue_script('theme_colorpicker_script', $template_url . '/framework/admin/layout/js/colorpicker.js', array('jquery'), false );	wp_enqueue_script('theme_eye_script', $template_url . '/framework/admin/layout/js/eye.js', array('jquery'), false );	wp_enqueue_script('prettyphoto', $template_url . '/framework/admin/layout/js/jquery.prettyPhoto.js', array('jquery'), false );	wp_enqueue_script('selectbox', $template_url . '/framework/admin/layout/js/jquery.selectBox.min.js', array('jquery'), false );	wp_enqueue_script('datepicker', $template_url . '/framework/admin/layout/js/jquery.ui.datepicker.js', array('jquery-ui-core'), false );		wp_register_script('options-media', $template_url.'/framework/admin/layout/js/media.js', array('jquery'), '1.0.0', true);	wp_localize_script('options-media', 'options_media', array('title' => __('Upload or Choose Your Image', THEME_SLUG), 'button' => __('Insert Image', THEME_SLUG)));	wp_enqueue_script('options-media');	}add_action('admin_init', 'theme_options_admin_enqueue');function theme_admin_custom_styles() {	echo '<style>#theme-options .theme-options-header{background:url('.get_option(THEME_SLUG.'_options_header_image').') left top no-repeat !important;}</style>';}add_action('admin_head', 'theme_admin_custom_styles');?>